import React, { useState } from "react";
import Form from "../layouts/Form";
import { Grid } from "@material-ui/core";
import { Input, Select, Button } from "../../controls";
const customers = [
  { id: 0, title: "Select" },
  { id: 1, title: "Customer1" },
  { id: 2, title: "Customer2" },
  { id: 3, title: "Customer3" },
  { id: 4, title: "Customer4" },
];

const pMethods = [
  { id: "none", title: "Select" },
  { id: "Cash", title: "Cash" },
  { id: "Card", title: "Card" },
];

// Random 6 so
const generateOrderNumber = () =>
  Math.floor(100000 + Math.random() * 900000).toString();

const getFreshModelObject = () => ({
  orderMasterId: 0,
  orderNumber: generateOrderNumber(),
  customerId: 0,
  pMethod: "none",
  gTotal: 0,
  deletedOrderItemIds: "",
  orderDetails: [],
});

export default function OrderForm() {
  const [value, setValue] = useState(getFreshModelObject());
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setValue({ ...value, [name]: value });
  };
  return (
    <Form>
      <Grid container>
        <Grid item xs={6}>
          {/* .MuiFormControl-root */}
          <Input
            disabled
            label="Order number"
            name="orderNumber"
            value={value.orderNumber}
          />
          {/* .MuiFormControl-root */}
          <Select
            label="Customer"
            name="CustomerId"
            value={value.customerId}
            options={customers}
          ></Select>
        </Grid>
        <Grid item xs={6}>
          {/* .MuiFormControl-root */}
          <Input
            disabled
            label="Grand total"
            name="gTotal"
            value={value.gTotal}
          />
          {/* .MuiFormControl-root */}
          <Select
            label="Payment method"
            name="pMethods"
            value={value.pMethod}
            options={pMethods}
          ></Select>
        </Grid>
      </Grid>
    </Form>
  );
}
