import React from "react";
import Form from "../layouts/Form";
import { Grid } from "@material-ui/core";
import Input from "../../controllers/Input";
import Select from "../../controllers/Select";

export default function OrderForm() {
  return (
    <Form>
      <Grid container>
        <Grid item xs={6}>
          <Input label="Order number" name="orderNumber" />
          <Select
            label="Customer"
            name="CustomerId"
            options={[
              { id: 0, title: "Select" },
              { id: 1, title: "Customer1" },
              { id: 2, title: "Customer2" },
              { id: 3, title: "Customer3" },
              { id: 4, title: "Customer4" },
            ]}
          ></Select>
        </Grid>
        <Grid item xs={6}>
          <Input label="Grand total" name="gTotal" />
        </Grid>
      </Grid>
    </Form>
  );
}
