import {
  Button,
  ButtonGroup,
  IconButton,
  List,
  ListItem,
  ListItemSecondaryAction,
  ListItemText,
  Paper,
  makeStyles,
} from "@material-ui/core";
import DeleteTwoToneIcon from "@material-ui/icons/DeleteTwoTone";
import { roundTo2DecimalPoint } from "../../utils";

const useStyles = makeStyles((theme) => ({
  paperRoot: {
    margin: "15px 0px",
  },
}));

export default function OrderedFoodItem(props) {
  const { values, setValues } = props;
  let orderedFoodItems = values.orderDetails;

  // Update quantity base on index
  const updateQuantity = (index, value) => {
    let x = { ...values };
    let foodItem = x.orderDetails[index];
    if (foodItem.quantity + value > 0) {
      foodItem.quantity += value;
      // console.log(x);
      setValues({ ...x });
    }
  };

  // Select other items which indexes are !== deleted index
  const removeFoodItem = (index, orderDetailId) => {
    let x = { ...values };
    x.orderDetails = x.orderDetails.filter((_, i) => i !== index);
    setValues({ ...x });
  };
  return (
    // List all FoodItems in OrderedFoodItem (orderDetails)
    <List>
      {orderedFoodItems.map((item, index) => (
        <Paper key={item.foodItemId}>
          {/* <Paper key={index}> */}
          <ListItem>
            <ListItemText
              primary={item.foodItemName}
              primaryTypographyProps={{
                component: "h1",
                style: {
                  fontSize: "1.2em",
                  fontWeight: "500",
                },
              }}
              secondary={
                <>
                  <ButtonGroup size="small">
                    <Button onClick={(e) => updateQuantity(index, -1)}>
                      -
                    </Button>
                    <Button disabled>{item.quantity}</Button>
                    <Button onClick={(e) => updateQuantity(index, 1)}>+</Button>
                  </ButtonGroup>
                  <span>
                    {"$" +
                      roundTo2DecimalPoint(item.quantity * item.foodItemPrice)}
                  </span>
                </>
              }
              secondaryTypographyProps={{
                component: "div", // if not: show error <p> can not contains <div>
              }}
            ></ListItemText>
            <ListItemSecondaryAction>
              <IconButton
                disableRipple
                onClick={(e) => removeFoodItem(index, item.orderDetailId)}
              >
                <DeleteTwoToneIcon></DeleteTwoToneIcon>
              </IconButton>
            </ListItemSecondaryAction>
          </ListItem>
        </Paper>
      ))}
    </List>
  );
}
