import React, { useState } from "react";
import Form from "../layouts/Form";
import {
  Grid,
  InputAdornment,
  makeStyles,
  ButtonGroup,
  Button as MuiButton,
  Typography,
} from "@material-ui/core";
import { Input, Select, Button } from "../../controls";
import ReplayIcon from "@material-ui/icons/Replay";
import RestaurantIcon from "@material-ui/icons/Restaurant";
const customers = [
  { id: 0, title: "Select" },
  { id: 1, title: "Customer1" },
  { id: 2, title: "Customer2" },
  { id: 3, title: "Customer3" },
  { id: 4, title: "Customer4" },
];

const pMethods = [
  { id: "none", title: "Select" },
  { id: "Cash", title: "Cash" },
  { id: "Card", title: "Card" },
];

const useStyles = makeStyles((theme) => ({
  adornmentText: {
    "& .MuiTypography-root": {
      color: "#f3b33d",
      fontWeight: "bolder",
      fontSize: "1.5em",
    },
  },
  submitButtonGroup: {
    "& .MuiButton-label": {
      textTransform: "none",
      textAlign: "center",
    },
    "&:hover": {
      backgroundColor: "#f3b33d",
    },
    margin: theme.spacing(1),
    backgroundColor: "#f3b33d",
    color: "#000",
  },
}));

export default function OrderForm(props) {
  const { values, errors, handleInputChange } = props;
  const classes = useStyles();

  return (
    <Form>
      <Grid container>
        <Grid item xs={6}>
          {/* .MuiFormControl-root */}
          <Input
            disabled
            label="Order number"
            name="orderNumber"
            value={values.orderNumber}
            InputProps={{
              startAdornment: (
                <InputAdornment
                  className={classes.adornmentText}
                  position="start"
                >
                  #
                </InputAdornment>
              ),
            }}
          />
          {/* .MuiFormControl-root */}
          <Select
            label="Customer"
            name="customerId"
            value={values.customerId}
            options={customers}
            onChange={handleInputChange}
          ></Select>
        </Grid>
        <Grid item xs={6}>
          {/* .MuiFormControl-root */}
          <Input
            disabled
            label="Grand total"
            name="gTotal"
            value={values.gTotal}
            InputProps={{
              startAdornment: (
                <InputAdornment
                  className={classes.adornmentText}
                  position="start"
                >
                  $
                </InputAdornment>
              ),
            }}
          />
          {/* .MuiFormControl-root */}
          <Select
            label="Payment method"
            name="pMethod"
            value={values.pMethod}
            options={pMethods}
            onChange={handleInputChange}
          ></Select>
          <ButtonGroup className={classes.submitButtonGroup}>
            <MuiButton
              endIcon={<RestaurantIcon></RestaurantIcon>}
              size="large"
              type="submit"
            >
              Submit
            </MuiButton>
            <MuiButton
              size="small"
              startIcon={<ReplayIcon></ReplayIcon>}
            ></MuiButton>
          </ButtonGroup>
        </Grid>
      </Grid>
    </Form>
  );
}
