import {
  IconButton,
  InputBase,
  List,
  ListItem,
  ListItemText,
  Paper,
} from "@material-ui/core";
import React, { useState, useEffect } from "react";
import { createAPIEndpoint, ENDPOINTS } from "../../api";
import SearchTwoToneIcon from "@material-ui/icons/SearchTwoTone";

export default function SearchFoodItem() {
  const [foodItems, setFoodItems] = useState([]);

  useEffect(() => {
    createAPIEndpoint(ENDPOINTS.FOODITEM)
      .fetchAll()
      .then((res) => {
        setFoodItems(res.data);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <>
      <Paper>
        <SearchTwoToneIcon></SearchTwoToneIcon>
        <InputBase placeholder="Search food item">
          <IconButton>sdf</IconButton>
        </InputBase>
      </Paper>
      <List>
        {foodItems.map((foodItem, index) => (
          <ListItem key={index}>
            <ListItemText
              primary={foodItem.foodItemName}
              secondary={foodItem.price}
            ></ListItemText>
          </ListItem>
        ))}
      </List>
    </>
  );
}
