import React, { useState, useEffect } from "react";
import Form from "../layouts/Form";
import {
  Grid,
  InputAdornment,
  makeStyles,
  ButtonGroup,
  Button as MuiButton,
} from "@material-ui/core";
import { Input, Select, Button } from "../../controls";
import ReplayIcon from "@material-ui/icons/Replay";
import RestaurantIcon from "@material-ui/icons/Restaurant";
import ReorderIcon from "@material-ui/icons/Reorder";
import { createAPIEndpoint, ENDPOINTS } from "../../api";
import { roundTo2DecimalPoint } from "../../utils";

// const customers = [
//   { id: 0, title: "Select" },
//   { id: 1, title: "Customer1" },
//   { id: 2, title: "Customer2" },
//   { id: 3, title: "Customer3" },
//   { id: 4, title: "Customer4" },
// ];

const pMethods = [
  { id: "none", title: "Select" },
  { id: "Cash", title: "Cash" },
  { id: "Card", title: "Card" },
];

const useStyles = makeStyles((theme) => ({
  // style the icons of orderNumber and gTotal
  adornmentText: {
    "& .MuiTypography-root": {
      color: "#f3b33d",
      fontWeight: "bolder",
      fontSize: "1.2em",
    },
  },
  submitButtonGroup: {
    "& .MuiButton-label": {
      textTransform: "none",
    },
    "&:hover": {
      backgroundColor: "#f3b33d",
    },
    margin: theme.spacing(1),
    backgroundColor: "#f3b33d",
    color: "#000",
  },
}));

export default function OrderForm(props) {
  const { values, setValues, errors, handleInputChange } = props;
  const classes = useStyles();

  const [customers, setCustomers] = useState([]);

  useEffect(() => {
    createAPIEndpoint(ENDPOINTS.CUSTOMER)
      .fetchAll()
      .then((res) => {
        let customers = res.data.map((customer) => ({
          id: customer.customerId,
          title: customer.customerName,
        }));
        customers = [{ id: 0, title: "Select" }].concat(customers);
        setCustomers(() => customers); // use arrow functions
      })
      .catch((err) => console.log(err));
  }, []);

  useEffect(() => {
    /*
    reduce: execute the callback function on each element inside the array
    reduce: output a single value
    tempTotal is the var to store the result after each iteration
    init tempTotal = 0
    */

    let gTotal = values.orderDetails.reduce((tempTotal, item) => {
      return roundTo2DecimalPoint(
        tempTotal + item.quantity * item.foodItemPrice
      );
    }, 0);
    setValues({ ...values, gTotal: gTotal });
  }, [JSON.stringify(values.orderDetails)]);

  const submitOrder = (e) => {};

  return (
    <Form onSubmit={submitOrder}>
      <Grid container>
        <Grid item xs={6}>
          {/* .MuiFormControl-root */}
          <Input
            disabled
            label="Order number"
            name="orderNumber"
            value={values.orderNumber}
            InputProps={{
              startAdornment: (
                <InputAdornment
                  className={classes.adornmentText}
                  position="start"
                >
                  #
                </InputAdornment>
              ),
            }}
          />
          {/* .MuiFormControl-root */}
          <Select
            label="Customer"
            name="customerId"
            value={values.customerId}
            options={customers}
            onChange={handleInputChange}
          ></Select>
        </Grid>
        <Grid item xs={6}>
          {/* .MuiFormControl-root */}
          <Input
            disabled
            label="Grand total"
            name="gTotal"
            value={values.gTotal}
            InputProps={{
              startAdornment: (
                <InputAdornment
                  className={classes.adornmentText}
                  position="start"
                >
                  $
                </InputAdornment>
              ),
            }}
          />
          {/* .MuiFormControl-root */}
          <Select
            label="Payment method"
            name="pMethod"
            value={values.pMethod}
            options={pMethods}
            onChange={handleInputChange}
          ></Select>
          <ButtonGroup className={classes.submitButtonGroup}>
            {/* Submit and Reset button */}
            <MuiButton
              endIcon={<RestaurantIcon></RestaurantIcon>}
              size="large"
              type="submit"
            >
              Submit
            </MuiButton>
            <MuiButton
              size="small"
              startIcon={<ReplayIcon></ReplayIcon>}
            ></MuiButton>
          </ButtonGroup>
          {/* Order button */}
          <Button size="large" startIcon={<ReorderIcon></ReorderIcon>}>
            Orders
          </Button>
        </Grid>
      </Grid>
    </Form>
  );
}
