import React, { useEffect, useState } from "react";
import OrderForm from "./OrderForm";
import { useForm } from "../../hooks/useForm";
import { Grid } from "@material-ui/core";
import SearchFoodItem from "./SearchFoodItem";
import OrderedFoodItem from "./OrderedFoodItems";
import { createAPIEndpoint, ENDPOINTS } from "../../api";

// Random 6 so
const generateOrderNumber = () =>
  Math.floor(100000 + Math.random() * 900000).toString();

const getFreshModelObject = () => ({
  orderMasterId: 0,
  orderNumber: generateOrderNumber(),
  customerId: 0,
  pMethod: "none",
  gTotal: 0,
  deletedOrderItemIds: "",
  foodItemName: "",
  orderDetails: [],
});

export default function Order() {
  const {
    values,
    setValues,
    errors,
    setErrors,
    handleInputChange,
    resetFormControls,
  } = useForm(getFreshModelObject);

  const addFoodItem = (foodItem) => {
    let orderDetail = {
      orderMasterId: values.orderMasterId,
      orderDetailId: 0,
      foodItemId: foodItem.foodItemId,
      quantity: 1,
      foodItemPrice: foodItem.foodItemPrice,
      foodItemName: foodItem.foodItemName || "",
    };
    setValues({
      ...values,
      orderDetails: [...values.orderDetails, orderDetail],
    });
  };
  const removeFoodItem = (index, orderDetailId) => {
    let x = { ...values };
    // Select others items which indexes are !== deleted index
    x.orderDetails = x.orderDetails.filter((_, i) => i !== index);
    setValues({ ...x });
  };

  return (
    <Grid container>
      <Grid item xs={12}>
        <OrderForm {...{ values, errors, handleInputChange }}></OrderForm>
      </Grid>
      <Grid item xs={6}>
        <SearchFoodItem
          {...{ addFoodItem, orderedFoodItems: values.orderDetails }}
        ></SearchFoodItem>
      </Grid>
      <Grid item xs={6}>
        <OrderedFoodItem
          {...{
            orderedFoodItems: values.orderDetails,
            removeFoodItem,
            values,
            setValues,
          }}
        ></OrderedFoodItem>
      </Grid>
    </Grid>
  );
}
