import React, { useEffect, useState } from "react";
import OrderForm from "./OrderForm";
import { useForm } from "../../hooks/useForm";
import { Grid } from "@material-ui/core";
import SearchFoodItem from "./SearchFoodItem";
import OrderedFoodItem from "./OrderedFoodItems";
import { createAPIEndpoint, ENDPOINTS } from "../../api";

// Random 6 so
const generateOrderNumber = () =>
  Math.floor(100000 + Math.random() * 900000).toString();

const getFreshModelObject = () => ({
  orderMasterId: 0,
  orderNumber: generateOrderNumber(),
  customerId: 0,
  pMethod: "none",
  gTotal: 0,
  deletedOrderItemIds: "",
  foodItemName: "",
  orderDetails: [],
});

export default function Order() {
  const {
    values,
    setValues,
    errors,
    setErrors,
    handleInputChange,
    resetFormControls,
  } = useForm(getFreshModelObject);

  const [foodName, setFoodName] = useState("");
  const [foodId, setFoodId] = useState(0);

  const addFoodItem = (foodItem) => {
    let orderDetail = {
      orderMasterId: values.orderMasterId,
      orderDetailId: 0,
      foodItemId: foodItem.foodItemId,
      quantity: 1,
      foodItemPrice: foodItem.foodItemPrice,
      foodItemName: foodName || "none",
    };
    setFoodId(() => orderDetail.foodItemId);
    // console.log("tren" + foodId);
    setValues({
      ...values,
      orderDetails: [...values.orderDetails, orderDetail],
    });
  };

  // call api get by id to set FoodItemName
  useEffect(() => {
    createAPIEndpoint(ENDPOINTS.FOODITEM)
      .fetchById(foodId)
      .then((res) => {
        setFoodName(() => res.data.foodItemName);
        console.log(res.data);
      })
      .catch((err) => console.log(err));
  }, [foodId]);

  return (
    <Grid container>
      <Grid item xs={12}>
        <OrderForm {...{ values, errors, handleInputChange }}></OrderForm>
      </Grid>
      <Grid item xs={6}>
        <SearchFoodItem {...{ addFoodItem }}></SearchFoodItem>
      </Grid>
      <Grid item xs={6}>
        <OrderedFoodItem
          {...{ orderedFoodItems: values.orderDetails }}
        ></OrderedFoodItem>
      </Grid>
    </Grid>
  );
}
